def test_hamming_distance_no_difference():
    assert hamming_distance(17, 17) == 0  # Same numbers

def test_hamming_distance_all_difference():
    assert hamming_distance(0, 255) == 8  # 0 -> 00000000, 255 -> 11111111 (all bits different)

def test_hamming_distance_some_difference():
    assert hamming_distance(1, 4) == 2  # 1 -> 0001, 4 -> 0100 (2 bits different)

def test_hamming_distance_mixed_numbers():
    assert hamming_distance(7, 10) == 3  # 7 -> 0111, 10 -> 1010 (3 bits different)

def test_hamming_distance_with_larger_numbers():
    assert hamming_distance(1024, 2048) == 2  # 1024 -> 10000000000, 2048 -> 100000000000 (2 bits different)

