test_meetings_end_and_start_at_same_time()
test_complex_schedule()

def test_longest_unique_substring_example1():
    assert longest_unique_substring("abcabcbb") == (3, 0)

def test_longest_unique_substring_example2():
    assert longest_unique_substring("bbbbb") == (1, 0)

def test_longest_unique_substring_example3():
    assert longest_unique_substring("pwwkew") == (3, 2)

def test_longest_unique_substring_with_mixed_characters():
    assert longest_unique_substring("dvdf") == (3, 1)

def test_longest_unique_substring_with_empty_string():
    assert longest_unique_substring("") == (0, 0)

def test_longest_unique_substring_with_single_character():
    assert longest_unique_substring("a") == (1, 0)

def test_longest_unique_substring_with_non_repeating_characters():
    assert longest_unique_substring("abcdef") == (6, 0)

def test_longest_unique_substring_with_all_repeating_characters():
    assert longest_unique_substring("aaaaaa") == (1, 0)

test_longest_unique_substring_example1()
test_longest_unique_substring_example2()
test_longest_unique_substring_example3()
test_longest_unique_substring_with_mixed_characters()
test_longest_unique_substring_with_empty_string()
test_longest_unique_substring_with_single_character()
test_longest_unique_substring_with_non_repeating_characters()
test_longest_unique_substring_with_all_repeating_characters()

def test_single_word():
    assert most_frequent_words("apple") == "apple"

def test_simple_case():
    assert most_frequent_words("banana apple apple") == "apple"

def test_tied_frequencies():
    assert most_frequent_words("hello world hello world") == ["hello", "world"]

def test_mixed_case():
    assert most_frequent_words("Hello HELLO hello world") == "hello"

def test_punctuation():
    assert most_frequent_words("Hello, world! Hello... world???") == ["hello", "world"]

def test_no_words():
    assert most_frequent_words("!!!...") == []

def test_empty_string():
    assert most_frequent_words("") == []

test_single_word()
test_simple_case()
test_tied_frequencies()
test_mixed_case()
test_punctuation()
test_no_words()
test_empty_string()
